const submitButton=document.querySelector('.submit-review-btn');function changeRatingOnClick(stars){stars.forEach(star=>{star.addEventListener('click',(e)=>{submitButton.removeAttribute("disabled")
let i=j=stars.indexOf(star);for(i;i>=0;--i){stars[i].classList.remove('active-star')
stars[i].innerHTML=inactiveStar;}
for(j;j<stars.length;++j){stars[j].classList.add('active-star')
stars[j].innerHTML=activeStar;}});});}
function showRatingOnHover(stars){stars.map((star)=>{star.addEventListener('mouseover',()=>{let i=j=stars.indexOf(star);for(i;i>=0;--i){stars[i].classList.remove('hover-star')
stars[i].innerHTML=inactiveStar;}
for(j;j<stars.length;++j){stars[j].classList.add('hover-star')
stars[j].innerHTML=activeStar;}});});}
document.addEventListener('DOMContentLoaded',function(){submitButton.setAttribute("disabled","")
const ratingStars=[...document.getElementsByClassName("rating-star")];const ratingInput=document.querySelector(".star-rating-input");changeRatingOnClick(ratingStars);ratingInput.addEventListener('mouseleave',function(){ratingStars.map(star=>{star.classList.remove('hover-star');if(star.classList.contains('active-star')){star.innerHTML=activeStar;}else{star.innerHTML=inactiveStar;}})})})